root.decodeArray:

var len_$(member) = binReader.ReadInt32();
var arr_$(member) = new $(className)[len_$(member)];
for (int i = 0; i < len_$(member); i++)
{
    arr_$(member)[i] = $(decode)
}
$(member) = new ReadOnlyCollection<$(className)>(arr_$(member));

